mapper.select:
SELECT DISTINCT i.apartmentname, Concat(c.sidoname, ' ', c.gugunname, ' ', c.dongname) location, i.buildyear, d.dealamount, Concat(d.dealyear, '.', d.dealmonth, '.', d.dealday) date, d.area, d.floor, i.lat, i.lng, (SELECT Count(*) FROM house_like WHERE aptcode = i.aptcode) house_like_count, c.dongcode, i.aptcode FROM houseinfo AS i LEFT JOIN dongcode AS c ON i.dongcode = c.dongcode LEFT JOIN housedeal AS d ON i.aptcode = d.aptcode LEFT JOIN house_like AS hl ON i.aptcode = hl.aptcode WHERE c.dongcode = #{dongCode}
AND (SELECT Cast(REPLACE(dealamount, ',', '') AS UNSIGNED)) >= #{lowDealAmount}
AND (SELECT Cast(REPLACE(dealamount, ',', '') AS UNSIGNED)) <= #{highDealAmount}
AND (SELECT Cast(area AS UNSIGNED)) >= #{lowArea}
AND (SELECT Cast(area AS UNSIGNED)) <= #{highArea}
AND d.dealyear >= Year(Now()) - #{year}

SELECT DISTINCT i.apartmentname, Concat(c.sidoname, ' ', c.gugunname, ' ', c.dongname) location, i.buildyear, d.dealamount, Concat(d.dealyear, '.', d.dealmonth, '.', d.dealday) date, d.area, d.floor, i.lat, i.lng, (SELECT Count(*) FROM house_like WHERE aptcode = i.aptcode) house_like_count, c.dongcode, i.aptcode FROM houseinfo AS i LEFT JOIN dongcode AS c ON i.dongcode = c.dongcode LEFT JOIN housedeal AS d ON i.aptcode = d.aptcode LEFT JOIN house_like AS hl ON i.aptcode = hl.aptcode WHERE i.aptcode IN (SELECT aptcode FROM houseinfo WHERE apartmentname LIKE concat('%', #{apartMentName}, '%'))
AND (SELECT Cast(REPLACE(dealamount, ',', '') AS UNSIGNED)) >= #{lowDealAmount}
AND (SELECT Cast(REPLACE(dealamount, ',', '') AS UNSIGNED)) <= #{highDealAmount}
AND (SELECT Cast(area AS UNSIGNED)) >= #{lowArea}
AND (SELECT Cast(area AS UNSIGNED)) <= #{highArea}
AND d.dealyear >= Year(Now()) - #{year}
