mapper.update:
		update question
		set subject = #{subject}, content = #{content}
    	where question_no = #{questionNo} and user_id = #{userId}

		update question
		set answer = #{answer}
    	where question_no = #{questionNo}

		update question
		set answer = #{answer}
    	where question_no = #{questionNo}

		update question
		set answer = null
    	where question_no = #{questionNo}
